    text: string;
    /** The voice to use for generation */
    voice?: string;
    /** The output audio format */
    format?: 'mp3' | 'opus' | 'aac' | 'flac' | 'wav' | 'pcm';
    /** The speed of the generated audio (0.25 to 4.0) */
    speed?: number;
    /** Provider-specific options for TTS generation */
